const Container = styled(motion.div)`
  .line-anim .line path {
    stroke-dasharray: 2800;
    stroke-dashoffset: 2800;
    animation: aboutLineAnimation 2s forwards;
  }
  .aboutLine {
    position: relative;
    z-index: 1;
  }
  .aboutContent {
    position: relative;
    margin: 2% 15%;
    z-index: 5;
  }
  .question {
    color: white;
    text-align: center;
    font-size: 2rem;
    font-weight: bold;
  }
  .content {
    margin-top: 10rem;
    display: flex;
    justify-content: space-between;
    .left {
      width: 40%;

      .header {
        margin: 0 0 3rem;
        animation: leftContentAnimation 2s forwards;
        position: relative;
        right: 50rem;

        h2 {
          color: white;
          font-weight: lighter;
          margin: 0;
          i {
            font-size: 1.5rem;
            transition: 0.1s;
            padding: 0.5rem;
            :hover {
              scale: 1.3;
              cursor: url("https://i.postimg.cc/ydY8PznW/rsz-exclusion-2.png "),
                auto;
            }
          }
        }
        .line {
          width: 100%;
          height: 0.1rem;
          background-color: white;
          margin-top: 0.5rem;
        }
      }
      .aboutMe {
        animation-delay: 1.3s;
      }
      .education {
        animation-delay: 1.5s;
      }
      .work {
        animation-delay: 1.7s;
      }
    }
    .right {
      width: 60%;
      svg {
        filter: drop-shadow(6px 6px 12px #22c59f);
        path {
          stroke: #22c59f;
          fill: none;
          stroke-dasharray: 1000;
          stroke-dashoffset: 1000;
          animation: textAnim 10s linear forwards;
        }
      }
      h1 {
        margin: 0;
        padding: 0;
      }
      .aboutMe,
      .work,
      .education {
        p {
          background-color: #1c1c1c;
          color: #6dabed;
          line-height: 2;
          font-size: 1.2rem;
        }
      }
    }
  }
  @keyframes textAnim {
    from {
      stroke-dashoffset: 1000;
    }
    to {
      stroke-dashoffset: 0;
    }
  }
  @keyframes aboutLineAnimation {
    from {
      stroke-dashoffset: 2800;
    }
    to {
      stroke-dashoffset: 0;
    }
  }
  @keyframes leftContentAnimation {
    from {
      right: 50rem;
    }
    to {
      right: 0rem;
    }
  }
  @media only screen and (max-width: 650px) {
    .aboutContent {
      margin: 3%;
    }
    .content {
      margin-top: 20%;
      display: flex;
      flex-direction: column;
      .left {
        width: 100%;
        margin-right: 5%;
        display: flex;
        .header {
          margin: 1%;
          h2 {
            font-size: 1.2rem;
            i {
              font-size: 1rem;
            }
          }
        }
      }
      .right {
        width: 100%;
        text-align: center;
        .aboutMe,
        .education,
        .work {
          svg {
            margin: 5% 3% 1% 3%;
          }
          p {
            font-size: 1rem;
            text-align: left;
          }
        }
      }
    }
  }
`;
export default About;
